		return false;
	}
	if (count < 1)
	{
		count = 1;
	}
	if (count > 32)
	{
		count = 32;
	}

	var opIndex = 4;
	for (var i = 0; i < count; i++)
	{
		var afbSignal = afbElement.getAfbSignalByOpIndex(opIndex);
